import React, { Component } from "react";
import {
  View,
  Text,
  TouchableOpacity,
  TextInput,
  StyleSheet,
  Button,
} from "react-native";

class SignUp extends Component {
  constructor(props) {
    super(props);
    this.username = "";
    this.password = "";
    this.confirmPwd = "";
  }

  state = {
    username: String,
    password: String,
    confirmPwd: String,
  };

  handleUsername = (text) => {
    this.setState({ username: text });
    this.toggleDisable();
  };

  handlePassword = (text) => {
    this.setState({ password: text });
    this.toggleDisable();
  };

  checkPassword = (text) => {
    this.setState({ confirmPwd: text });
    this.toggleDisable();
  };

  toggleDisable = () => {
    if (
      this.state.username === "" ||
      this.state.password === "" ||
      this.state.confirmPwd !== this.state.password
    ) {
      this.disabled = true;
    } else {
      this.disabled = false;
    }
  };

  disable = true;

  render = () => {
    return (
      <View>
        <TextInput
          style={styles.textinput}
          placeholder="Username"
          onChangeText={this.handleUsername}
        />
        <TextInput
          style={styles.textinput}
          placeholder="Password"
          textContentType="password"
          onChangeText={this.handlePassword}
        />
        <TextInput
          style={styles.textinput}
          placeholder="Confirm Password"
          onChangeText={this.checkPassword}
        />
        <Button title="Sign Up" disabled={this.disable} />
      </View>
    );
  };
}

export default SignUp;

const styles = StyleSheet.create({
  textinput: {
    height: 30,
    margin: 5,
    borderBottomWidth: 1,
  },
});
